--
-- Cấu trúc bảng cho bảng `tbl_posts`
--

CREATE TABLE `tbl_posts` (
  `post_id` int(11) NOT NULL,
  `post_title` varchar(10000) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `cat_id` int(11) NOT NULL,
  `post_desc` varchar(1000) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `author` enum('1','2','3') COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `created_date` int(11) NOT NULL,
  `content` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `thumb_url` varchar(100) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `status` enum('1','2') COLLATE utf8mb4_vietnamese_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_vietnamese_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_posts`
--

INSERT INTO `tbl_posts` (`post_id`, `post_title`, `cat_id`, `post_desc`, `author`, `created_date`, `content`, `thumb_url`, `status`) VALUES
(30, 'Đánh giá chi tiết Vsmart Star 5: Smartphone giá rẻ nhưng chất lượng không hề rẻ', 2, 'Vsmart Star 5 có thể được xem là một trong những mẫu smartphone nổi bật nhất trong phân khúc dưới 3 triệu đồng vì thiết bị đạt đúng như những gì người dùng đã kỳ vọng trước đó', '3', 1618307512, '<p><img alt=\"\" src=\"https://media-cdn.laodong.vn/Storage/NewsPortal/2021/4/2/895028/1.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vsmart Star 5 c&oacute; thể được xem l&agrave; một trong những mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;nổi bật nhất trong ph&acirc;n kh&uacute;c dưới 3 triệu đồng v&igrave; thiết bị đạt đ&uacute;ng như những g&igrave; m&igrave;nh đ&atilde;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ky-vong-gi-ve-vsmart-star-5-1336421\" target=\"_blank\" title=\"kỳ vọng\" type=\"kỳ vọng\">kỳ vọng</a>&nbsp;trước đ&oacute;. B&ecirc;n cạnh&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-hieu-nang-vsmart-star-5-1338684\" target=\"_blank\" title=\"hiệu năng\" type=\"hiệu năng\">hiệu năng</a>&nbsp;ổn định,&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/danh-gia-pin-vsmart-star-5-1338043\" target=\"_blank\" title=\"thời lượng pin\" type=\"thời lượng pin\">thời lượng pin</a>&nbsp;tr&acirc;u b&ograve; th&igrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd?p=duoi-2-trieu\" target=\"_blank\" title=\"smartphone giá rẻ\" type=\"smartphone giá rẻ\">smartphone gi&aacute; rẻ</a>&nbsp;mới đến từ&nbsp;<a href=\"https://www.thegioididong.com/dtdd-vsmart\" target=\"_blank\" title=\"Vsmart\" type=\"Vsmart\">Vsmart</a>&nbsp;c&ograve;n c&oacute; nhiều điểm th&uacute; vị kh&aacute;c m&agrave; m&igrave;nh sẽ đề cập trong b&agrave;i đ&aacute;nh gi&aacute; chi tiết Vsmart Star 5 ở dưới đ&acirc;y. C&ugrave;ng bắt đầu th&ocirc;i n&agrave;o c&aacute;c bạn.</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>1. Vi&ecirc;n pin 5.000 mAh của Vsmart Star 5 trụ được 8 tiếng hoạt động li&ecirc;n tục</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Như m&igrave;nh đ&atilde; n&oacute;i ở tr&ecirc;n, một trong những điều khiến m&igrave;nh ấn tượng nhất về chiếc Vsmart Star 5 chắc chắn l&agrave; vi&ecirc;n pin dung lượng 5.000 mAh của em n&oacute;. Nếu c&aacute;c bạn đang cảm thấy con số 8 tiếng hoạt động li&ecirc;n tục của Vsmart Star 5 l&agrave; kh&ocirc;ng c&oacute; g&igrave; qu&aacute; to t&aacute;t th&igrave; c&aacute;c bạn n&ecirc;n suy nghĩ lại nh&eacute; v&igrave; b&agrave;i test pin m&agrave; Vsmart Star 5 phải g&aacute;nh kh&ocirc;ng hề đơn giản đ&acirc;u v&agrave; sau đ&acirc;y sẽ l&agrave; điều kiện của b&agrave;i test pin :</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trải nghiệm 4 t&aacute;c vụ xoay v&ograve;ng gồm: Chơi Li&ecirc;n Qu&acirc;n (<a href=\"https://drive.google.com/drive/u/0/folders/1-fE4Ilaz3bJNBliWU4vvpvsXIbDFMuLz\" rel=\"nofollow\" target=\"_blank\" title=\"Thiết lập chi tiết\" type=\"Thiết lập chi tiết\">Thiết lập chi tiết</a>), xem YouTube, lướt Facebook v&agrave; x&agrave;i Chrome.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mỗi t&aacute;c vụ sử dụng 1 tiếng đồng hồ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Độ s&aacute;ng m&agrave;n h&igrave;nh: 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Cắm tai nghe c&oacute; d&acirc;y xuy&ecirc;n suốt v&agrave; bật &acirc;m lượng l&ecirc;n 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bật WiFi v&agrave; c&aacute;c th&ocirc;ng b&aacute;o từ mạng x&atilde; hội.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật tiết kiệm pin, m&agrave;n h&igrave;nh th&iacute;ch ứng, GPS v&agrave; Bluetooth.</span></li>\r\n	<li><span style=\"font-size:16px;\">Chấm từ 100% đến 0%.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/03/27/1338684/bang-test-pin-vsmart-star-5-1_800x353.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Vsmart Star 5 trụ được 8 tiếng 14 ph&uacute;t sau b&agrave;i test pin (theo ti&ecirc;u chuẩn của Ismart)</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mặc d&ugrave; b&agrave;i test pin (theo ti&ecirc;u chuẩn của Ismart) cực k&igrave; khắc nghiệt nhưng đại diện đến từ Vsmart vẫn c&oacute; thể trụ được với thời gian l&ecirc;n đến 8 tiếng 14 ph&uacute;t, quả l&agrave; một con số ấn tượng. Khi sử dụng thực tế (kh&ocirc;ng c&oacute; điều kiện khắc nghiệt g&igrave; đ&acirc;u nh&eacute;) th&igrave; m&igrave;nh thấy thiết bị ho&agrave;n to&agrave;n c&oacute; thể trụ được gần 1 ng&agrave;y rưỡi với c&aacute;c t&aacute;c vụ cơ bản học tập/l&agrave;m việc của m&igrave;nh v&agrave; m&igrave;nh kh&ocirc;ng c&oacute; chơi game nh&eacute; c&aacute;c bạn. Nếu m&agrave; c&oacute; th&ecirc;m t&aacute;c vụ chơi game th&igrave; m&aacute;y trụ được hết 1 ng&agrave;y.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/03/24/1338043/vsmart-star-5-luot-web_800x450.png\" style=\"width:100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Nếu như chỉ sử dụng Vsmart Star 5 với c&aacute;c t&aacute;c vụ cơ bản m&agrave; kh&ocirc;ng chơi game, thiết bị ho&agrave;n to&agrave;n c&oacute; thể trụ được gần 1 ng&agrave;y rưỡi.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vsmart Star 5 c&ograve;n được trang bị t&iacute;nh năng sạc nhanh 15W, vượt trội hơn nhiều so với một số đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute; khi chỉ được trang bị sạc nhanh 10W m&agrave; th&ocirc;i. Vsmart cũng rất h&agrave;o ph&oacute;ng khi tặng k&egrave;m cho ch&uacute;ng ta bộ c&aacute;p sạc nhanh 18W trong hộp của Vsmart Star 5 v&agrave; m&igrave;nh cũng đ&atilde; test tốc độ sạc của thiết bị lu&ocirc;n, kết quả m&igrave;nh thu được như sau:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/03/27/1338684/test-sac-pin-cua-may-vsmart-star-5_800x450.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Thời gian để sạc đầy vi&ecirc;n pin của Vsmart Star 5 (từ 0% đến 100%) l&agrave; 2 tiếng 46 ph&uacute;t.</span></em></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>2. Hiệu năng của Vsmart Star 5: Ngon trong tầm gi&aacute;</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Trước khi đi v&agrave;o phần đ&aacute;nh gi&aacute; hiệu năng th&igrave; ch&uacute;ng ta h&atilde;y c&ugrave;ng điểm sơ qua một ch&uacute;t về th&ocirc;ng số cấu h&igrave;nh của Vsmart Star 5:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">CPU: MediaTek Helio G35 (x&acirc;y dựng dựa tr&ecirc;n tiến tr&igrave;nh 12 nm v&agrave; bao gồm 8 nh&acirc;n).</span></li>\r\n	<li><span style=\"font-size:16px;\">GPU: IMG PowerVR GE8320.</span></li>\r\n	<li><span style=\"font-size:16px;\">RAM: 3 GB.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bộ nhớ trong: 32 GB hoặc 64 GB.</span></li>\r\n	<li><span style=\"font-size:16px;\">Hệ điều h&agrave;nh: VOS 4.0 (dựa tr&ecirc;n Android 11).</span></li>\r\n	<li><span style=\"font-size:16px;\">Pin: 5.000 mAh (c&oacute; hỗ trợ sạc nhanh 15W).</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">So với những đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute; th&igrave; th&ocirc;ng số cấu h&igrave;nh của Vsmart Star 5 kh&ocirc;ng thua k&eacute;m qu&aacute; nhiều nhưng liệu trải nghiệm hiệu năng thực tế tr&ecirc;n chiếc smartphone c&oacute; tốt hay kh&ocirc;ng? Trước ti&ecirc;n th&igrave; m&igrave;nh sẽ sử dụng c&aacute;c phần mềm như 3D Mark, PC Mark v&agrave; Geek Bench 5 để chấm điểm hiệu năng cho Vsmart Star 5. Kết quả m&igrave;nh thu được như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">- GeekBench 5:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Điểm đơn nh&acirc;n: 163 điểm.</span></li>\r\n	<li><span style=\"font-size:16px;\">Điểm đa nh&acirc;n: 963 điểm.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/03/27/1338684/diem-geekbench5-vsmart-star-5_800x600.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Điểm GeekBench 5 của Vsmart Star 5.</span></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>3. Camera của Vsmart Star 5: Chất lượng ổn, đủ d&ugrave;ng</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">So với người tiền nhiệm th&igrave; Vsmart Star 5 được trang bị th&ecirc;m 1 camera v&agrave; tăng số lượng ống k&iacute;nh l&ecirc;n th&agrave;nh 3. Hệ thống camera n&agrave;y sẽ c&oacute; th&ocirc;ng số cụ thể như sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Camera ch&iacute;nh với độ ph&acirc;n giải 13 MP.</span></li>\r\n	<li><span style=\"font-size:16px;\">Camera macro với độ ph&acirc;n giải 2 MP.</span></li>\r\n	<li><span style=\"font-size:16px;\">Camera x&oacute;a ph&ocirc;ng với độ ph&acirc;n giải 2 MP.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">B&ecirc;n trong giao diện camera, Vsmart Star 5 c&ograve;n t&iacute;nh to&aacute;n cho ch&uacute;ng ta số lượng h&igrave;nh ảnh c&ograve;n lại m&agrave; m&aacute;y c&oacute; thể chụp v&agrave; lưu trữ được. C&ograve;n b&acirc;y giờ th&igrave; ch&uacute;ng ta c&ugrave;ng bắt đầu đ&aacute;nh gi&aacute; chất lượng ảnh chụp tr&ecirc;n hệ thống 3 camera của Vsmart Star 5 nh&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Ở điều kiện đầy đủ s&aacute;ng th&igrave; camera ch&iacute;nh 13 MP của Vsmart Star 5 đ&atilde; l&agrave;m tốt vai tr&ograve; của m&igrave;nh. H&igrave;nh ảnh c&oacute; m&agrave;u sắc đẹp, ch&acirc;n thực v&agrave; khả năng c&acirc;n bằng s&aacute;ng cũng rất ấn tượng. Mặc d&ugrave; chi tiết c&oacute; bị mờ đi khi m&igrave;nh ph&oacute;ng ảnh to ra nhưng nh&igrave;n chung với một m&aacute;y gi&aacute; rẻ th&igrave; điều n&agrave;y ho&agrave;n to&agrave;n chấp nhận được.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/du-sang-vsmart-star-5-2_800x600.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Ảnh chụp trong điều kiện đủ s&aacute;ng của Vsmart Star 5.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">M&igrave;nh c&oacute; chụp thử một số tấm h&igrave;nh ở điều kiện ngược s&aacute;ng th&igrave; Vsmart Star 5 vẫn cho ra những bức h&igrave;nh c&oacute; chất lượng ổn so với tầm gi&aacute;. May m&agrave; h&igrave;nh chỉ bị nh&ograve;e chi tiết một ch&uacute;t th&ocirc;i chứ chưa bị ch&aacute;y s&aacute;ng, mặc d&ugrave; m&igrave;nh chụp l&uacute;c trời c&ograve;n nắng rất gắt.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/nguoc-sang-vsmart-star-5-1_800x1067.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Nhưng may l&agrave; h&igrave;nh chụp trong điều kiện ngược s&aacute;ng kh&ocirc;ng bị t&igrave;nh trạng ch&aacute;y s&aacute;ng.</span></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>4. Thiết kế của Vsmart Star 5 kh&ocirc;ng qu&aacute; ph&aacute; c&aacute;ch nhưng vẫn g&acirc;y ấn tượng với mặt lưng c&aacute;ch điệu</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thiết kế của Vsmart Star 5 kh&ocirc;ng c&oacute; qu&aacute; nhiều sự đột ph&aacute;. Từ người d&ugrave;ng trẻ tuổi cho đến người gi&agrave;, ai cũng c&oacute; thể sử dụng thiết bị Vsmart v&agrave; m&igrave;nh tin l&agrave; Vsmart Star 5 ho&agrave;n to&agrave;n c&oacute; thể tiếp tục ph&aacute;t huy ưu điểm n&agrave;y từ những người đ&agrave;n anh đi trước.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/vsmart-star-5-mau_800x450.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Vsmart Star 5 sở hữu thiết kế th&acirc;n thiện với mọi người d&ugrave;ng.</span></em></p>\r\n\r\n<p>Thiết kế tổng thể của Vsmart Star 5 gần như tương đồng với một số mẫu smartphone gi&aacute; rẻ kh&aacute;c tr&ecirc;n thị trường hiện nay với cụm camera sau được đặt ở một g&oacute;c c&ugrave;ng cảm biến v&acirc;n tay ở mặt lưng. To&agrave;n bộ th&acirc;n m&aacute;y được l&agrave;m từ nhựa n&ecirc;n khối lượng tổng thể của Vsmart Star 5 cũng kh&ocirc;ng qu&aacute; nặng, khoảng 196.26 gram.</p>\r\n\r\n<p>&Agrave; c&oacute; một điểm m&agrave; m&igrave;nh rất th&iacute;ch tr&ecirc;n mặt lưng của chiếc smartphone n&agrave;y đ&oacute; l&agrave; họa tiết những đường v&acirc;n nổi, lượn s&oacute;ng tỏa ra từ phần camera. Kh&ocirc;ng biết c&aacute;c bạn thấy như thế n&agrave;o nhưng m&igrave;nh thấy họa tiết n&agrave;y đang m&ocirc; tả cho c&aacute;i t&ecirc;n &#39;Star&#39; của m&aacute;y, kiểu tỏa s&aacute;ng như những v&igrave; sao ấy. Ngo&agrave;i ra th&igrave; mặt lưng của Vsmart Star 5 cũng gi&uacute;p hạn chế b&aacute;m bẩn, mồ h&ocirc;i hoặc dấu v&acirc;n tay khi m&igrave;nh cầm m&aacute;y.<img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/vsmart-star-5-mat-lung_800x450.png\" style=\"width:100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Họa tiết đầy th&uacute; vị tr&ecirc;n mặt lưng của Vsmart Star 5</span></em></p>\r\n\r\n<p>M&igrave;nh cảm thấy rất thoải m&aacute;i khi cầm chiếc Vsmart Star tr&ecirc;n tay, nhờ v&agrave;o c&aacute;c phần g&oacute;c cạnh được bo cong mềm mại. K&iacute;ch thước tổng thể của m&aacute;y kh&aacute; nhỏ gọn (cụ thể l&agrave; 163.91 x 75.67 x 9.1 mm), dễ d&agrave;ng cho ph&eacute;p m&igrave;nh sử dụng m&aacute;y bằng một tay (tất nhi&ecirc;n với những ai tay to th&ocirc;i nh&eacute; c&aacute;c bạn).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/vsmart-star-5-cam-nam_800x450.png\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Cảm gi&aacute;c cầm nắm của Vsmart Star 5 l&agrave; rất thoải m&aacute;i, dễ chịu</span></em></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>5. Vsmart Star 5 sở hữu m&agrave;n h&igrave;nh với chất lượng hiển thị tốt</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tiếp nối phần thiết kế mặt trước ở tr&ecirc;n th&igrave; giờ ch&uacute;ng ta h&atilde;y c&ugrave;ng nh&igrave;n sơ qua về th&ocirc;ng số m&agrave;n h&igrave;nh của Vsmart Star 5 nh&eacute;. Thiết bị sở hữu một tấm nền IPS LCD với k&iacute;ch thước l&ecirc;n đến 6.52 inch c&ugrave;ng độ ph&acirc;n giải l&agrave; HD+ (720 x 1.600 pixel). C&oacute; thể n&oacute;i đại diện đến từ Vsmart sở hữu những th&ocirc;ng số m&agrave;n h&igrave;nh tương đồng so với những đối thủ trong c&ugrave;ng ph&acirc;n kh&uacute;c gi&aacute; nhưng trải nghiệm thực tế tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y sẽ như thế n&agrave;o?</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/01/1339985/vsmart-star-5-thong-so-man-hinh_800x450.png\" style=\"width: 100%; height: auto;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Vsmart Star 5 c&oacute; tấm nền IPS LCD với k&iacute;ch thước l&ecirc;n đến 6.52 inch c&ugrave;ng độ ph&acirc;n giải l&agrave; HD+</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Theo trải nghiệm của c&aacute; nh&acirc;n m&igrave;nh, m&agrave;n h&igrave;nh của Vsmart Star 5 c&oacute; chất lượng hiển thị ở mức tốt (so với mức gi&aacute; của m&aacute;y), độ s&aacute;ng cao, m&agrave;u sắc đẹp c&ugrave;ng mức độ chi tiết ổn. C&oacute; điều v&igrave; m&aacute;y sử dụng tấm nền IPS LCD n&ecirc;n cho g&oacute;c nh&igrave;n kh&ocirc;ng qu&aacute; rộng, v&iacute; dụ khi nhiều người c&ugrave;ng xem phim/video tr&ecirc;n chiếc smartphone n&agrave;y th&igrave; m&igrave;nh nghĩ những người ở hai b&ecirc;n sẽ rất kh&oacute; quan s&aacute;t được nội dung.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tốc độ phản hồi của m&agrave;n h&igrave;nh Vsmart Star 5 m&igrave;nh đ&aacute;nh gi&aacute; ở mức tốt, l&acirc;u l&acirc;u vẫn c&oacute; độ trễ nhưng điều n&agrave;y kh&ocirc;ng ảnh hưởng qu&aacute; nhiều đến trải nghiệm của m&igrave;nh. M&igrave;nh nghĩ c&oacute; thể l&agrave; do phần mềm của Vsmart Star 5 chưa được tối ưu tốt hoặc l&agrave; dung lượng RAM của m&aacute;y chỉ c&oacute; 3 GB n&ecirc;n đ&ocirc;i khi tốc độ xử l&yacute; c&aacute;c t&aacute;c vụ c&oacute; chậm trễ một x&iacute;u.</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Tổng kết</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vsmart Star 5 chắc chắn sẽ l&agrave; một sự lựa chọn kh&ocirc;ng thể n&agrave;o bỏ qua nếu như bạn đang cảm thấy bị bội thực bởi những chiếc smartphone &#39;ngon, bổ, rẻ&#39; đến từ Trung Quốc. M&igrave;nh tin l&agrave; trong tầm gi&aacute; dưới 3 triệu th&igrave; chiếc smartphone gi&aacute; rẻ mới đến từ Vsmart sẽ mang đến cho bạn những trải nghiệm th&uacute; vị v&agrave; phần n&agrave;o gi&uacute;p bạn thay đổi c&aacute;ch nh&igrave;n về chất lượng của h&agrave;ng made in Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vậy c&aacute;c bạn nghĩ g&igrave; về Vsmart Star 5? Nếu như c&aacute;c bạn muốn m&igrave;nh l&agrave;m g&igrave; về con m&aacute;y n&agrave;y th&igrave; đừng ngần ngại m&agrave; để lại cảm nghĩ của bạn dưới phần b&igrave;nh luận của b&agrave;i viết nh&eacute;. Cảm ơn c&aacute;c bạn.</span></p>\r\n', 'uploadFiles/danh-gia-chi-tiet-vsmart-star-5.png', '2'),
(31, 'Tất Tần Tật: Các mẫu smartphone Vivo có thời lượng pin sử dụng liên tục trên 10 tiếng, đỉnh nhất là hơn 13 tiếng', 2, 'Nếu bạn đang tìm một chiếc smartphone có thời lượng pin khủng thì dế cưng đến từ Vivo chính là một trong những sự lựa chọn sáng giá', '3', 1618331930, '<p><span style=\"font-size:16px;\">Nếu bạn đang t&igrave;m một chiếc smartphone c&oacute; thời lượng pin khủng th&igrave; dế cưng đến từ Vivo ch&iacute;nh l&agrave; một trong những sự lựa chọn s&aacute;ng gi&aacute;.M&igrave;nh đ&atilde; kh&aacute; bất ngờ với số lượng sản phẩm g&oacute;p mặt trong b&agrave;i viết tổng hợp tất tần tật c&aacute;c smartphone Vivo c&oacute; thời lượng pin sử dụng li&ecirc;n tục tr&ecirc;n 10 tiếng, được đ&aacute;nh gi&aacute; theo ti&ecirc;u chuẩn của&nbsp;<a href=\"https://www.thegioididong.com/\" target=\"_blank\" title=\"Thế Giới Di Động\" type=\"Thế Giới Di Động\">Thế Giới Di Động</a>&nbsp;dưới đ&acirc;y. H&atilde;y c&ugrave;ng t&igrave;m hiểu ngay để xem con số đ&oacute; l&agrave; bao nhi&ecirc;u nh&eacute;?</span></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>1. Vivo Y50</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Điều kiện test pin Vivo Y50:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trải nghiệm 4 t&aacute;c vụ xoay v&ograve;ng gồm: Chơi Li&ecirc;n Qu&acirc;n, xem YouTube, lướt Facebook v&agrave; x&agrave;i Chrome.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mỗi t&aacute;c vụ sử dụng 1 tiếng đồng hồ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Độ s&aacute;ng m&agrave;n h&igrave;nh:&nbsp; 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Cắm tai nghe c&oacute; d&acirc;y xuy&ecirc;n suốt.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bật WiFi v&agrave; c&aacute;c th&ocirc;ng b&aacute;o từ mạng x&atilde; hội.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật tiết kiệm pin, m&agrave;n h&igrave;nh th&iacute;ch ứng, GPS v&agrave; Bluetooth.</span></li>\r\n	<li><span style=\"font-size:16px;\">Chấm từ 100% đến 0%.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343069/vivoy50_1_805x462-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Kết quả b&agrave;i test pin Vivo Y50 theo ti&ecirc;u chuẩn của Thế Giới Di Động.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong điều kiện như tr&ecirc;n th&igrave; chiếc m&aacute;y cho thời lượng sử dụng li&ecirc;n tục l&agrave; 10 tiếng 11 ph&uacute;t. Đ&uacute;ng l&agrave; Vivo c&oacute; kh&aacute;c, pin l&uacute;c n&agrave;o cũng l&agrave;m người d&ugrave;ng cảm thấy thỏa m&atilde;n, d&ugrave; m&igrave;nh đ&atilde; mở độ s&aacute;ng l&ecirc;n mức 100% v&agrave; sử dụng với cường độ li&ecirc;n tục.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2020/04/29/1252536/vivoy50-25_1280x720-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Vivo Y50 cho thời lượng sử dụng li&ecirc;n tục 10 tiếng 11 ph&uacute;t</span></em></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>2. Vivo Y12s</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Điều kiện test pin Vivo Y12s:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trải nghiệm 4 t&aacute;c vụ xoay v&ograve;ng gồm: Chơi Li&ecirc;n Qu&acirc;n, xem YouTube, lướt Facebook v&agrave; x&agrave;i Chrome.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mỗi t&aacute;c vụ sử dụng 1 tiếng đồng hồ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Độ s&aacute;ng m&agrave;n h&igrave;nh: 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Cắm tai nghe c&oacute; d&acirc;y xuy&ecirc;n suốt.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bật WiFi v&agrave; c&aacute;c th&ocirc;ng b&aacute;o từ mạng x&atilde; hội.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật tiết kiệm pin, m&agrave;n h&igrave;nh th&iacute;ch ứng, GPS v&agrave; Bluetooth.</span></li>\r\n	<li><span style=\"font-size:16px;\">Chấm từ 100% đến 0%.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343069/vivoy12s_804x461-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Kết quả b&agrave;i test pin Vivo Y12s theo ti&ecirc;u chuẩn của Thế Giới Di Động</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Ở những b&agrave;i viết trước m&igrave;nh cũng đ&atilde; c&oacute; n&oacute;i rằng, Vivo l&agrave; một trong những h&atilde;ng sản xuất ưu &aacute;i về dung lượng pin cho những đứa con tinh thần của m&igrave;nh. Điều đ&oacute; cũng kh&ocirc;ng ngoại lệ với Vivo Y12s khi chiếc m&aacute;y sở hữu vi&ecirc;n pin 5.000 mAh.&nbsp;Mặc d&ugrave; điều kiện test khắc nghiệt như vậy nhưng Vivo Y12s vẫn cho thời lượng sử dụng đ&aacute;ng ngưỡng mộ, đạt hơn 10 tiếng li&ecirc;n tục.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2020/10/31/1303466/dsc009581_1280x720.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Vivo Y12 cho thời lượng sử dụng li&ecirc;n tục 10 tiếng 04 ph&uacute;t</span></em></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>3. Vivo Y30</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Điều kiện test pin Vivo Y30:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trải nghiệm 4 t&aacute;c vụ xoay v&ograve;ng gồm: Chiến Li&ecirc;n Qu&acirc;n, xem YouTube, lướt Facebook v&agrave; d&ugrave;ng tr&igrave;nh duyệt (Chrome).</span></li>\r\n	<li><span style=\"font-size:16px;\">Mỗi t&aacute;c vụ 1 tiếng đồng hồ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Đ&egrave;n nền 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Cắm tai nghe c&oacute; d&acirc;y xuy&ecirc;n suốt.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật chế độ tiết kiệm pin hay m&agrave;n h&igrave;nh th&iacute;ch ứng.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bật chế độ hiệu suất cao.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mở WiFi v&agrave; c&aacute;c th&ocirc;ng b&aacute;o từ mạng x&atilde; hội.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật GPS, Bluetooth v&agrave; NFC.</span></li>\r\n	<li><span style=\"font-size:16px;\">Đo từ 100% đến 0%.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343069/vivoy30_805x618-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Kết quả b&agrave;i test pin Vivo Y30 theo ti&ecirc;u chuẩn của Thế Giới Di Động</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vi&ecirc;n pin 5.000 mAh của Vivo Y30 cho thời gian sử dụng li&ecirc;n tục tới 13 tiếng đồng hồ. Th&ocirc;ng qua b&agrave;i test hiệu suất cao như vậy cho thấy pin của Vivo Y30 rất tr&acirc;u. Thực tế khi sử dụng, m&igrave;nh chỉ để độ s&aacute;ng m&agrave;n h&igrave;nh ở 60% m&agrave; th&ocirc;i v&igrave; m&agrave;n h&igrave;nh m&aacute;y rất s&aacute;ng v&agrave; m&igrave;nh chỉ sử dụng để nghe, gọi, lướt web, giải tr&iacute; nhẹ nh&agrave;ng. Tuy nhi&ecirc;n phải đến ng&agrave;y thứ 3 m&igrave;nh mới đem m&aacute;y đi sạc pin.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2020/06/08/1261568/vivoy30-3_1280x720-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Vivo Y30 cho thời lượng sử dụng li&ecirc;n tục 13 tiếng 17 ph&uacute;t</span></em></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>4. Vivo Y20</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Điều kiện test pin Vivo Y20:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trải nghiệm 4 t&aacute;c vụ xoay v&ograve;ng gồm: Chiến Li&ecirc;n Qu&acirc;n, xem YouTube, lướt Facebook v&agrave; d&ugrave;ng tr&igrave;nh duyệt (Chrome).</span></li>\r\n	<li><span style=\"font-size:16px;\">Mỗi t&aacute;c vụ 1 tiếng đồng hồ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Đ&egrave;n nền 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">&Acirc;m lượng tai nghe 100%.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật chế độ tiết kiệm pin hay m&agrave;n h&igrave;nh th&iacute;ch ứng.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bật chế độ hiệu suất cao.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mở kết nối 4G v&agrave; c&aacute;c th&ocirc;ng b&aacute;o từ mạng x&atilde; hội.</span></li>\r\n	<li><span style=\"font-size:16px;\">Kh&ocirc;ng bật GPS, Bluetooth v&agrave; NFC.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343069/pinvivo1_805x502-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Kết quả b&agrave;i test pin Vivo Y20 theo ti&ecirc;u chuẩn của Thế Giới Di Động</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Kết quả cho b&agrave;i test l&agrave; hơn 10 tiếng một ch&uacute;t x&iacute;u khi sử dụng hỗn hợp. Su&yacute;t ch&uacute;t nữa th&igrave; qu&ecirc;n, Vivo Y20 sở hữu cho m&igrave;nh cục pin 5.000 mAh. Ngo&agrave;i ra, Vivo Y20 cho thời gian chơi game đến hơn 10 tiếng li&ecirc;n tục.&nbsp;Bản th&acirc;n m&igrave;nh đ&atilde; test thời gian chơi game v&agrave; sử dụng của chiếc điện thoại n&agrave;y v&agrave; phải c&ocirc;ng nhận l&agrave; qu&aacute; tr&acirc;u. M&igrave;nh chỉ để độ s&aacute;ng ở mức 70% v&agrave; kh&ocirc;ng bật loa ngo&agrave;i th&igrave; đ&aacute;nh Li&ecirc;n Qu&acirc;n được hơn 10 tiếng li&ecirc;n tục.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2020/09/11/1289054/vivoy20xanh-1_800x450.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Vivo Y20 cho thời lượng sử dụng li&ecirc;n tục 10 tiếng 30 ph&uacute;t</span></em></p>\r\n', 'uploadFiles/t_1280x720-300x200.png', '2'),
(32, 'Realme 8i lộ ảnh thiết kế kèm cấu hình trước ngày ra mắt: Pin 4.400mAh, màn hình AMOLED và hỗ trợ kết nối 5G', 2, 'Realme RMX2205 5G dự kiến có tên gọi là Realme 8i, máy vừa được phát hiện trên trang web chứng nhận TENAA với các thông số kỹ thuật kèm một vài hình ảnh.', '3', 1618332332, '<h2>&nbsp;</h2>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343062/realme-rmx2205-tenaa-1_824x535-800-resize.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Realme RMX2205 tr&ecirc;n TENAA</span></em></p>\r\n\r\n<p><span style=\"font-size:22px;\">Realme RMX2205 5G dự kiến c&oacute; t&ecirc;n gọi l&agrave; Realme 8i, m&aacute;y vừa được ph&aacute;t hiện tr&ecirc;n trang web chứng nhận TENAA với c&aacute;c th&ocirc;ng số kỹ thuật k&egrave;m một v&agrave;i h&igrave;nh ảnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Giới thạo tin phỏng đo&aacute;n rằng chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"điện thoại\">điện thoại</a>&nbsp;n&agrave;y c&oacute; thể l&agrave; một phần của d&ograve;ng Realme 8. Ch&uacute;ng ta đ&atilde; thấy thiết kế của&nbsp;<a href=\"https://www.thegioididong.com/dtdd/realme-8\" target=\"_blank\" title=\"Điện thoại Realme 8\">Realme 8</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/realme-8-pro\" target=\"_blank\" title=\"Điện thoại Realme 8 Pro\" type=\"Điện thoại Realme 8 Pro\">Realme 8 Pro</a>, nhưng c&aacute;c h&igrave;nh ảnh tr&ecirc;n TENAA cho thấy đ&acirc;y kh&ocirc;ng phải l&agrave; một trong hai mẫu. Thay v&agrave;o đ&oacute;, Realme RMX2205 c&oacute; thể l&agrave; Realme 8i, dự kiến ​​sẽ sớm ra mắt tại Ấn Độ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><a href=\"https://www.thegioididong.com/dtdd-realme\" target=\"_blank\" title=\"Đặt mua smartphone Realme tại Thegioididong.com\">Realme</a>&nbsp;gần đ&acirc;y đ&atilde; tiết lộ rằng họ c&oacute; kế hoạch mang Realme 8 5G v&agrave; Realme 8i đến thị trường đ&ocirc;ng d&acirc;n thứ 2 thế giới n&agrave;y. Ngo&agrave;i ra, danh s&aacute;ch TENAA cũng cung cấp cho ch&uacute;ng ta một v&agrave;i th&ocirc;ng số kỹ thuật ch&iacute;nh của Realme 8i.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343062/realme-rmx2205-tenaa-4_1092x826-800-resize.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">H&igrave;nh ảnh cho thấy Realme 8i c&oacute; cụm 3 camera h&igrave;nh chữ nhật c&ugrave;ng nh&atilde;n hiệu &ldquo;DARE TO LEAP&rdquo; ở mặt sau. Thiết bị c&oacute; một lỗ kho&eacute;t ở g&oacute;c tr&ecirc;n b&ecirc;n tr&aacute;i để chứa camera selfie, n&uacute;t nguồn ở cạnh phải v&agrave; n&uacute;t chỉnh &acirc;m lượng ở cạnh tr&aacute;i. Realme 8i sẽ t&iacute;ch hợp cảm biến v&acirc;n tay dưới m&agrave;n h&igrave;nh, vậy n&ecirc;n c&oacute; thể tin rằng m&aacute;y sẽ được trang bị m&agrave;n h&igrave;nh AMOLED.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Danh s&aacute;ch TENAA tiết lộ Realme RMX2205 l&agrave; điện thoại hỗ trợ mạng 5G. Smartphone n&agrave;y c&oacute; k&iacute;ch thước 158.5 x 73.3 x 8.4 mm v&agrave; c&oacute; m&agrave;n h&igrave;nh rộng 6.43 inch. Thiết bị n&agrave;y c&oacute; pin 4.400 mAh, hỗ trợ 2 SIM v&agrave; chạy tr&ecirc;n Android 11.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn thấy thiết kế của Realme 8i thế n&agrave;o?</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'uploadFiles/realme-8i-1-_1920x1080-300x200.png', '2'),
(33, 'Nghe Đồn Là: Nokia X30 sẽ có viên pin khủng 5.850 mAh đi kèm sạc nhanh 22W (Đang cập nhật)', 2, 'Sau sự kiện ra mắt 6 chiếc smartphone cách đây không lâu, Nokia lại tiếp tục rò rỉ thêm một số thông tin mới nhất về Nokia X30', '3', 1618395663, '<p><span style=\"font-size:16px;\">Sau sự kiện ra mắt 6 chiếc smartphone c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, Nokia lại tiếp tục r&ograve; rỉ th&ecirc;m một số th&ocirc;ng tin mới nhất về Nokia X30. C&oacute; lẽ phi&ecirc;n bản tiền nhiệm&nbsp;<a href=\"https://www.thegioididong.com/dtdd/nokia-x10\" target=\"_blank\" title=\"Nokia X10\" type=\"Nokia X10\">Nokia X10</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/nokia-x20\" target=\"_blank\" title=\"Nokia X20\" type=\"Nokia X20\">Nokia X20</a>&nbsp;vẫn chưa đạt được sự&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/ky-vong-nokia-x10-1339895\" target=\"_blank\" title=\"Kỳ vọng Nokia X10\" type=\"Kỳ vọng Nokia X10\">kỳ vọng</a>&nbsp;của người h&acirc;m mộ như những g&igrave; đ&atilde;&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/nghe-don-la-nokia-x10-1339283\" target=\"_blank\" title=\"Nghe đồn là Nokia X10\" type=\"Nghe đồn là Nokia X10\">r&ograve; rỉ</a>&nbsp;trước đ&oacute; n&ecirc;n Nokia X30 được dự đo&aacute;n sẽ l&agrave; một phi&ecirc;n bản n&acirc;ng cấp hơn chăng? Trong b&agrave;i viết n&agrave;y, h&atilde;y c&ugrave;ng m&igrave;nh tổng hợp lại to&agrave;n bộ nghe đồn l&agrave; của Nokia X30 nh&eacute;!</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Lưu &yacute;: B&agrave;i viết sẽ được cập nhật li&ecirc;n tục khi c&oacute; th&ocirc;ng tin mới nhất nh&eacute;!</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Nokia X30 sẽ đi theo thiết kế an to&agrave;n hay ph&aacute; c&aacute;ch? (Th&ocirc;ng tin cập nhật ng&agrave;y 12/04/2021)</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong những sản phẩm ra mắt gần đ&acirc;y của m&igrave;nh, Nokia gần như kh&ocirc;ng c&oacute; sự thay đổi g&igrave; cho c&aacute;c dế cưng của m&igrave;nh cả. Nh&agrave; sản xuất n&agrave;y vẫn đi theo phong c&aacute;ch cực kỳ quen thuộc với c&aacute;c cạnh viền bo tr&ograve;n, chiếc logo Nokia ở cuối m&agrave;n h&igrave;nh, cụm camera tr&ograve;n ở mặt lưng... C&oacute; lẽ đ&atilde; đến l&uacute;c Nokia X30 được kho&aacute;c l&ecirc;n m&igrave;nh bộ &aacute;o mới rồi.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/08/1341798/dddd_1280x720-800-resize.jpg\" style=\"width: 100%; height: auto;\"  /><span style=\"font-size:16px;\"><em>Thiết kế c&oacute; phần quen thuộc của Nokia tr&ecirc;n Nokia X10 mới được ra mắt.</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trước khi tổ chức sự kiện ra mắt c&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, concept &#39;hụt&#39; của chiếc Nokia X10 đ&atilde; được lan truyền ch&oacute;ng mặt trong giới c&ocirc;ng nghệ v&agrave; đ&oacute;n nhận được nhiều sự t&aacute;n dương của người d&ugrave;ng. Biết đ&acirc;u đ&acirc;y ch&iacute;nh l&agrave; 1 ph&eacute;p thử của Nokia v&agrave; người được thừa hưởng thiết kế n&agrave;y ch&iacute;nh l&agrave; Nokia X30.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Nếu đ&uacute;ng l&agrave; như vậy, ch&uacute;ng ta sẽ c&oacute; một chiếc smartphone với ngoại h&igrave;nh v&ocirc; c&ugrave;ng độc đ&aacute;o. C&aacute;c cạnh viền xung quanh được l&agrave;m vu&ocirc;ng vức, g&oacute;c cạnh rất mạnh mẽ. Cụm camera tr&ograve;n ph&iacute;a sau được thay thế bằng h&igrave;nh thoi mới lạ, kh&ocirc;ng đụng h&agrave;ng ai. Ở ph&iacute;a trước, ch&uacute;ng ta vẫn sẽ c&oacute; một m&agrave;n h&igrave;nh đục lỗ tương tự như tr&ecirc;n Nokia X10 v&agrave; Nokia X20. M&igrave;nh tin rằng với thiết kế mới n&agrave;y, Nokia X30 sẽ dư sức tạo n&ecirc;n tiếng vang lớn trong thị trường smartphone.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/03/30/1339283/08_800x450.png\" style=\"width: 100%; height: auto;\"  /><em><span style=\"font-size:16px;\">Concept được mong chờ sẽ xuất hiện tr&ecirc;n Nokia X30. Nguồn: RcmT.</span></em></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>Nokia X30 sẽ được trang bị vi&ecirc;n pin khủng 5.850 mAh</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Một chứng nhận mới đ&acirc;y đ&atilde; tiết lộ 2 chiếc smartphone, mới c&oacute; thể thuộc d&ograve;ng G hoặc d&ograve;ng X của Nokia, mang số hiệu P660 v&agrave; SE681 sẽ ra mắt trong thời gian sắp tới v&agrave; c&oacute; dung lượng pin lần lượt l&agrave; 4.850 mAh v&agrave; 5.850 mAh. C&aacute; nh&acirc;n m&igrave;nh tin rằng Nokia X30 sẽ c&oacute; vi&ecirc;n pin 5.850 mAh. Bởi cho đến hiện tại, c&aacute;c sản phẩm của d&ograve;ng X lu&ocirc;n thuộc ph&acirc;n kh&uacute;c cao hơn d&ograve;ng G, n&ecirc;n việc được trang bị vi&ecirc;n pin lướn hơn cũng l&agrave; điều dễ hiểu.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/12/1342733/nokia-1_829x429-800-resize.jpg\" style=\"width: 100%; height: auto;\"  /><em><span style=\"font-size:16px;\">Th&ocirc;ng tin từ chứng nhận r&ograve; rỉ vi&ecirc;n pin của Nokia X30. Nguồn: NPU.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Nếu tin đồn tr&ecirc;n trở th&agrave;nh sự thật th&igrave; lời hứa mang đến dung lượng pin l&acirc;u d&agrave;i hơn tr&ecirc;n sản phẩm của m&igrave;nh của Nokia đang dần trở th&agrave;nh sự thật rồi đấy. Ngo&agrave;i ra, vi&ecirc;n pin 5.850 mAh tr&ecirc;n c&ograve;n được đi k&egrave;m với tốc độ sạc nhanh l&agrave; 22.52 W, hứa hẹn sẽ tối ưu thời gian sạc pin kha kh&aacute; đ&oacute;. Tuy nhi&ecirc;n nếu vi&ecirc;n pin của Nokia X30 chỉ ở mức 4.850 mAh th&igrave; em m&aacute;y n&agrave;y vẫn được hỗ trợ sạc nhanh 18.67 W, kh&ocirc;ng thua k&eacute;m g&igrave; đ&acirc;u nh&eacute;!</span></p>\r\n\r\n<h3><span style=\"font-size:22px;\"><strong>Nokia X30 sẽ ra mắt khi n&agrave;o v&agrave; gi&aacute; b&aacute;n l&agrave; bao nhi&ecirc;u?</strong></span></h3>\r\n\r\n<p><span style=\"font-size:16px;\">Nokia mới tổ chức sự kiện ra mắt kh&aacute; ho&agrave;nh tr&aacute;ng trong th&aacute;ng 4 n&agrave;y n&ecirc;n m&igrave;nh nghĩ x&aacute;c suất cho việc X30 được ra mắt trong v&ograve;ng 3 - 6 th&aacute;ng nữa kh&aacute; l&agrave; thấp. Tuy nhi&ecirc;n cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; khả năng cho sự xuất hiện sớm của em m&aacute;y n&agrave;y. Nếu đ&uacute;ng l&agrave; vậy th&igrave; đ&acirc;y c&oacute; thể l&agrave; phi&ecirc;n bản cải tiến của Nokia X20.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Điều đ&oacute; c&oacute; nghĩa l&agrave; em m&aacute;y của ch&uacute;ng ta cũng sẽ c&oacute; mức gi&aacute; cao hơn so với Nokia X20. Dựa tr&ecirc;n gi&aacute; b&aacute;n khởi điểm khoảng 9.6 triệu đồng (350 Euro) của Nokia X20, m&igrave;nh dự đo&aacute;n Nokia X30 sẽ c&oacute; mức ch&ecirc;nh lệch từ 1-2 triệu cho những n&acirc;ng cấp của m&igrave;nh. Đ&acirc;y vẫn l&agrave; con số ph&ugrave; hợp cho 1 chiếc smartphone tầm trung của nh&agrave; Nokia.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/08/1341850/03_800x450.jpg\" style=\"width: 100%; height: auto;\"  /><em><span style=\"font-size:16px;\">Liệu gi&aacute; b&aacute;n của Nokia X30 c&oacute; qu&aacute; cao so với người tiền nhiệm l&agrave; Nokia X20?</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&ograve;n trong trường hợp sau 1 năm nữa, Nokia X30 mới được tr&igrave;nh l&agrave;ng th&igrave; bạn c&oacute; thể y&ecirc;n t&acirc;m rằng mức gi&aacute; b&aacute;n của em m&aacute;y n&agrave;y sẽ kh&ocirc;ng ch&ecirc;nh qu&aacute; nhiều đ&acirc;u. Bởi l&uacute;c n&agrave;y, Nokia X30 sẽ l&agrave; phi&ecirc;n bản kế nhiệm của Nokia X20 n&ecirc;n gi&aacute; b&aacute;n cũng chỉ quanh quẩn đ&acirc;u đ&oacute; 350 Euro, tương đương với thế hệ tiền nhiệm m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>B&agrave;i viết vẫn đang được cập nhật li&ecirc;n tục ngay khi c&oacute; th&ocirc;ng tin mới nhất về Nokia X30. C&aacute;c bạn nhớ theo d&otilde;i nh&eacute;.</em></strong></span></p>\r\n', 'uploadFiles/thumb1_1280x720-300x200.png', '2'),
(34, 'Samsung dự kiến sẽ ra mắt Galaxy S21 FE vào tháng 8 tới để khỏa lấp khoảng trống mà Galaxy Note để lại', 2, 'Sau một số rò rỉ và tin đồn, cuối cùng công ty đã chính thức trình làng Galaxy S20 FE vào tháng 10', '3', 1618395530, '<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343005/samsung_galaxy_s21_fe-2_800x450.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Render Galaxy S21 FE</span></em></p>\r\n\r\n<h2><span style=\"font-size:16px;\">Năm ngo&aacute;i,&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Các sản phẩm của Samsung\">Samsung</a>&nbsp;đ&atilde; ph&aacute;t h&agrave;nh một phi&ecirc;n bản mới cho d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s20\" target=\"_blank\" title=\"Galaxy S20\" type=\"Galaxy S20\">Galaxy S20</a>&nbsp;của m&igrave;nh dưới t&ecirc;n gọi Galaxy S20 FE hoặc Fan Edition. Sau một số r&ograve; rỉ v&agrave; tin đồn, cuối c&ugrave;ng c&ocirc;ng ty đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s20-fan-edition\" target=\"_blank\" title=\"Galaxy S20 FE\">Galaxy S20 FE</a>&nbsp;v&agrave;o th&aacute;ng 10. Thiết bị n&agrave;y đ&atilde; g&acirc;y được tiếng vang lớn v&agrave; nhanh ch&oacute;ng trở th&agrave;nh một trong những&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Đặt mua smartphone tại Thegioididong.com\">smartphone</a>&nbsp;b&aacute;n chạy nhất của c&ocirc;ng ty v&agrave;o năm 2020.</span></h2>\r\n\r\n<p><span style=\"font-size:16px;\">V&igrave; l&yacute; do đ&oacute;, phi&ecirc;n bản kế nhiệm của Galaxy S20 FE rất c&oacute; thể sẽ được tung ra. Ch&uacute;ng ta đ&atilde; nghe c&aacute;c tin đồn về &nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21-fe\" target=\"_blank\" title=\"Chi tiết Galaxy S21 FE\" type=\"Chi tiết Galaxy S21 FE\">Galaxy S21 FE</a>&nbsp;kể từ th&aacute;ng 2, điều n&agrave;y hơi ngạc nhi&ecirc;n v&igrave; d&ograve;ng Galaxy S21 ch&iacute;nh thức ra mắt v&agrave;o th&aacute;ng 1/2021. C&oacute; vẻ như Samsung sẽ kh&ocirc;ng mất qu&aacute; nhiều thời gian để cho ra một phi&ecirc;n bản Fan Edition mới, ph&aacute;t triển từ d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s21\" target=\"_blank\" title=\"Chi tiết Samsung Galaxy S21 5G\">Galaxy S21 5G</a>.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mới đ&acirc;y, leaker Steve H (được biết đến với c&aacute;i t&ecirc;n OnLeaks) đ&atilde; tiết lộ rằng, thiết kế của chiếc Fan Edition mới sẽ giống như Galaxy S21 nhưng đi k&egrave;m với một cụm camera mới. Cụ thể hơn, module camera chữ nhật sẽ c&oacute; m&agrave;u sắc giống với mặt sau, thay v&igrave; kh&aacute;c m&agrave;u để tạo điểm nhấn</span>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/13/1343005/samsung_galaxy_s21_fe_600x523.jpg\" style=\"width: 100%; height: auto;\"  /></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Render Galaxy S21 FE</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Nguồn tin cũng n&oacute;i rằng, điện thoại sẽ c&oacute; k&iacute;ch thước 155.7 x 74.5 x 7.9 mm, tuy nhi&ecirc;n, độ d&agrave;y tổng thể tăng l&ecirc;n 9.3 mm khi t&iacute;nh cả độ d&agrave;y của module camera. Chiếc điện thoại n&agrave;y sẽ c&oacute; khung kim loại, dẫu vậy, mặt sau sẽ được l&agrave;m bằng nhựa v&agrave; Samsung phủ một lớp b&oacute;ng mờ l&ecirc;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Về thời gian ra mắt, b&aacute;o c&aacute;o h&ocirc;m nay (13/4) cho hay, Galaxy S21 FE sẽ ra mắt sớm hơn b&igrave;nh thường, v&agrave;o khoảng th&aacute;ng 8/2021 nhằm khỏa lấp khoảng trống m&agrave; d&ograve;ng Galaxy Note 21 để lại. Nếu bạn c&ograve;n nhớ th&igrave;&nbsp;trước đ&acirc;y, Samsung đ&atilde; từng x&aacute;c nhận sẽ kh&ocirc;ng ra mắt d&ograve;ng Galaxy Note thế hệ tiếp theo nữa v&igrave; thiếu hụt nguồn cung chip.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn sẵn s&agrave;ng đ&oacute;n nhận Galaxy S21 FE chứ?</span></p>\r\n', 'uploadFiles/samsung_galaxy_s21_fe-2_800x450-300x200.png', '2');
INSERT INTO `tbl_posts` (`post_id`, `post_title`, `cat_id`, `post_desc`, `author`, `created_date`, `content`, `thumb_url`, `status`) VALUES
(35, 'Kỳ vọng Huawei P50: Sử dụng màn hình 120 Hz, sẽ có một phiên bản chạy Snapdragon', 2, 'Chúng ta đã có những hình ảnh rò rỉ mới nhất về Huawei P50 - Chiếc smartphone mới nhất từ nhà Huawei đã dần hé lộ đã khiến anh em công nghệ đứng ngồi không yên', '3', 1618397540, '<p><span style=\"font-size:16px;\">Ch&uacute;ng&nbsp;ta đ&atilde; c&oacute; những h&igrave;nh ảnh r&ograve; rỉ mới nhất về&nbsp;<a href=\"https://www.thegioididong.com/dtdd/huawei-p50\" target=\"_blank\" title=\"Huawei P50\" type=\"Huawei P50\">Huawei P50</a>&nbsp;- Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;mới nhất từ nh&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-huawei\" target=\"_blank\" title=\"Huawei\" type=\"Huawei\">Huawei</a>&nbsp;đ&atilde; dần h&eacute; lộ đ&atilde; khiến anh em c&ocirc;ng nghệ đứng ngồi kh&ocirc;ng y&ecirc;n với thiết kế v&ocirc; c&ugrave;ng độc đ&aacute;o, cấu h&igrave;nh mạnh mẽ với Kirin 9000. V&igrave; vậy trước thời điểm m&aacute;y được ra mắt th&igrave; m&igrave;nh sẽ chia sẻ những kỳ vọng về Huawei P50 n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Huawei P50 sẽ c&oacute; cụm camera nhỏ hơn so với những h&igrave;nh ảnh tin đồn r&ograve; rỉ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mới đ&acirc;y Leaker nổi tiếng&nbsp;&nbsp;<a href=\"https://www.youtube.com/watch?v=fS7Md63uVeY\" rel=\"nofollow\" target=\"_blank\" title=\"Waqar Khan\" type=\"Waqar Khan\">Waqar Khan</a>&nbsp;đ&atilde; đăng tải những h&igrave;nh ảnh r&ograve; rỉ đầu ti&ecirc;n mới nhất của Huawei P50 với thiết kế&nbsp;v&ocirc; c&ugrave;ng sang trọng, bo cong c&aacute;c cạnh v&ocirc; c&ugrave;ng đẹp mắt. Đ&aacute;ng tiếc, chiếc m&aacute;y n&agrave;y kh&ocirc;ng c&oacute; m&agrave;n h&igrave;nh cong th&aacute;c nước v&agrave; đi k&egrave;m với một&nbsp;cụm camera thiết kế cực kỳ độc đ&aacute;o nhất tr&ecirc;n thị trường smartphone hiện nay.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tr&ecirc;n quan điểm c&aacute; nh&acirc;n của m&igrave;nh nhận định rằng cụm camera n&agrave;y bộc lộ qu&aacute; nhiều yếu điểm chẳng hạn như nh&igrave;n m&aacute;y hơi th&ocirc;, mất đi sự gọn g&agrave;ng v&agrave; thanh lịch vốn c&oacute; tr&ecirc;n c&aacute;c d&ograve;ng m&aacute;y của Huawei. Nh&igrave;n chung mặt lưng c&oacute; vẻ thiếu c&acirc;n đối v&agrave; thiếu sự đồng nhất l&agrave;m Huawei P50 tr&ocirc;ng chưa được thẩm mỹ cho lắm.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/12/1342652/a_1280x734-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Thiết kế r&ograve; tỉ của Huawei P50. Nguồn: Waqar Khan.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">M&igrave;nh kỳ vọng cụm camera cỉa Huawei P50 khi được ra mắt ch&iacute;nh thức sẽ nhỏ hơn, gọn hơn hoặc quay về thiết kế cụm camera h&igrave;nh tr&ograve;n như trước giờ đ&atilde; từng như vậy sẽ bảo đảm m&aacute;y vẫn giữ được sự thẩm mỹ v&agrave; gọn g&agrave;ng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Huawei P50 sẽ c&oacute; m&agrave;n h&igrave;nh cong v&agrave; tần số qu&eacute;t 144 Hz</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Ngay từ khi trải nghiệm m&agrave;n h&igrave;nh tr&ecirc;n những chiếc điện thoại cao cấp của Huawei m&igrave;nh ho&agrave;n to&agrave;n bị ấn tượng bởi những g&igrave; m&agrave; Huawei mang đến cho sản phẩm của m&igrave;nh. Theo tin đồn r&ograve; rỉ gần đ&acirc;y, m&aacute;y sẽ kh&ocirc;ng c&ograve;n được sử dụng m&agrave;n h&igrave;nh cong th&aacute;c nước ở mặt trước nữa m&agrave; thay v&agrave;o đ&oacute; l&agrave; m&agrave;n h&igrave;nh phẳng ho&agrave;n to&agrave;n.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&oacute; thể n&oacute;i m&agrave;n h&igrave;nh cong kiểu th&aacute;c nước tr&ecirc;n c&aacute;c thiết bị Huawei mang lại sự cao cấp v&agrave; sang trọng, cũng như ph&acirc;n biệt r&otilde; ph&acirc;n kh&uacute;c tầm trung v&agrave; ph&acirc;n kh&uacute;c cao cấp. Việc Huawei P50 trang bị m&agrave;n h&igrave;nh phẳng th&igrave; nh&igrave;n m&aacute;y tr&ocirc;ng cứ như những thiết bị tầm trung ấy.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/12/1342652/screen_1280x720-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Thay v&igrave; sử dụng m&agrave;n h&igrave;nh phẳng th&igrave; m&igrave;nh kỳ vọng Huawei sẽ sử dụng m&agrave;nh h&igrave;nh cong. </span></em></p>\r\n\r\n<p style=\"text-align: center;\"><em><span style=\"font-size:16px;\">Nguồn: Waqar Khan.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">N&ecirc;n m&igrave;nh kỳ vọng rằng Huawei P50 vẫn được trang bị m&agrave;n h&igrave;nh cong để tr&ocirc;ng m&aacute;y ra d&aacute;ng một chiếc m&aacute;y cao cấp hơn. B&ecirc;n cạnh đ&oacute; m&igrave;nh kỳ vọng m&aacute;y sẽ được trang bị m&agrave;n h&igrave;nh tần số qu&eacute;t 144 Hz để mang để cảm gi&aacute;c vuốt chạm mượt m&agrave;. C&oacute; thể n&oacute;i m&agrave;n h&igrave;nh đẹp phải đi k&egrave;m với tần số qu&eacute;t cao mới c&oacute; thể mang đến trải nghiệm tốt nhất.</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Huawei P50 sẽ c&oacute; một phi&ecirc;n bản chạy Snapdragon 888 v&agrave; hỗ trợ lại dịch vụ Google</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Nghe c&oacute; vẻ bất khả thi nhưng đ&acirc;y l&agrave; điều m&igrave;nh cũng như nhiều nhiều anh em c&ocirc;ng nghệ mong muốn c&oacute; tr&ecirc;n smartphone Huawei. Ch&uacute;ng ta đều biết Huawei P50 sẽ sử dụng con chip c&acirc;y nh&agrave; l&aacute; vườn Kirin 9000, nhưng m&igrave;nh kỳ vọng Huawei P50 sẽ được trang bị th&ecirc;m phi&ecirc;n bản sử dụng con chip Snapdragon 888 để mang lại hiệu năng v&agrave; trải nghiệm tốt hơn, bởi v&igrave; m&igrave;nh lu&ocirc;n tin tưởng hiệu năng đến từ những con chip Snapdragon.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">B&ecirc;n cạnh việc trang bị Snapdragon, m&igrave;nh hi vọng bằng một c&aacute;ch thần kỳ nao đ&oacute;, Huawei P50 sẽ hỗ trợ lại dịch vụ Google. Như vậy ch&uacute;ng ta c&oacute; thể thoải m&aacute;i trải nghiệm v&agrave; tối ưu được hết những c&ocirc;ng nghệ của Huawei P50.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/12/1342652/snapdragon-888-1280_1280x720-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">M&igrave;nh kỳ vọng bằng 1 ph&eacute;p m&agrave;u n&agrave;o đ&oacute; Huawei P50 sẽ sử dụng Snapdragon 888. Nguồn: Gsmarena.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&oacute; thể n&oacute;i nếu Huawei P50 c&oacute; một phi&ecirc;n bản Snapdragon 888 v&agrave; c&oacute; hỗ trợ dịch vụ Google th&igrave; m&igrave;nh đo&aacute;n m&aacute;y sẽ l&agrave; một c&uacute; hit lớn đối với cộng đồng người d&ugrave;ng c&ocirc;ng nghệ v&agrave; Huawei.</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Huawei P50 sẽ c&oacute; vi&ecirc;n pin 5.000 mAh đi k&egrave;m với sạc nhanh 66 W</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hiện tại vẫn chưa c&oacute; th&ocirc;ng tin g&igrave; nhiều về dung lượng pin của Huawei P50 nhưng m&igrave;nh kỳ vọng Huawei P50 sẽ được trang bị vi&ecirc;n pin dung lượng 5.000 mAh, để mang lại thời lượng sử dụng tốt hơn khi sử dụng với một cấu h&igrave;nh v&ocirc; c&ugrave;ng mạnh mẽ v&agrave; c&ocirc;ng nghệ ti&ecirc;n tiến</span>.&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/12/1342652/gsmarena_002_1200x709-800-resize.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">M&igrave;nh kỳ vọng Huawei P50 sẽ c&oacute; vi&ecirc;n pin 5.000 mAh. Nguồn: Waqar Khan.</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">B&ecirc;n cạnh đ&oacute; m&igrave;nh kỳ vọng th&ecirc;m rằng Huawei P50 sẽ được trang bị th&ecirc;m sạc nhanh 66 W để tối ưu thời gian sạc gi&uacute;p ch&uacute;ng ta c&oacute; thể cầm m&aacute;y thường xuy&ecirc;n hơn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:22px;\"><strong>Lời kết&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đ&oacute; l&agrave; những kỳ vọng của m&igrave;nh về Huawei P50 v&agrave; ch&uacute;ng ta h&atilde;y chờ xem đến ng&agrave;y ra mắt ch&iacute;nh thức của m&aacute;y để xem h&atilde;ng sẽ mang đến cho ch&uacute;ng ta bất ngờ g&igrave; nh&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&aacute;c bạn c&oacute; kỳ vọng g&igrave; về Huawei sắp tới? H&atilde;y để lại &yacute; kiến dưới phần b&igrave;nh luận nh&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cảm ơn c&aacute;c bạn đ&atilde; d&agrave;nh thời gian theo d&otilde;i b&agrave;i viết.</span></p>\r\n', 'uploadFiles/thumbhuawei_1280x720-300x200.png', '2'),
(36, 'Các iFan mong đợi điều này đã lâu rồi: iPhone 2023 sẽ không còn notch tai thỏ, sử dụng công nghệ Face ID ẩn dưới màn hình', 2, 'Nhà phân tích Ming-Chi Kuo mới đây tuyên bố rằng, Apple đang tìm cách đưa hệ thống Face ID ẩn dưới màn hình trên chiếc iPhone 2023.', '3', 1618395192, '<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/14/1343370/iphone-1_800x450.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Ảnh minh họa (Nguồn: MacRumors)</span></em></p>\r\n\r\n<p><span style=\"font-size:22px;\">Nh&agrave; ph&acirc;n t&iacute;ch Ming-Chi Kuo mới đ&acirc;y tuy&ecirc;n bố rằng,&nbsp;<a href=\"http://www.thegioididong.com/apple\" target=\"_blank\" title=\"Apple\">Apple</a>&nbsp;đang t&igrave;m c&aacute;ch đưa hệ thống Face ID ẩn dưới m&agrave;n h&igrave;nh tr&ecirc;n chiếc iPhone 2023.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Điều n&agrave;y sẽ cho ph&eacute;p c&ocirc;ng ty loại bỏ notch tai thỏ v&agrave; mang đến trải nghiệm to&agrave;n m&agrave;n h&igrave;nh thực sự. Tuy nhi&ecirc;n, ghi ch&uacute; của Kuo kh&ocirc;ng chứa bất kỳ chi tiết n&agrave;o kh&aacute;c về hệ thống Face ID dưới m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&aacute;c tin đồn cũng cho thấy, Apple đ&atilde; l&agrave;m việc tr&ecirc;n hệ thống Face ID dưới m&agrave;n h&igrave;nh trong một thời gian d&agrave;i. Mặc d&ugrave; những h&atilde;ng như&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"OPPO\" type=\"OPPO\">OPPO</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/samsung\" target=\"_blank\" title=\"Samsung\" type=\"Samsung\">Samsung</a>&nbsp;đ&atilde; giới thiệu c&ocirc;ng nghệ camera ẩn dưới m&agrave;n h&igrave;nh, nhưng hệ thống Face ID c&oacute; c&aacute;c th&agrave;nh phần kh&aacute;c như bộ ph&aacute;t hồng ngoại, dot blaster (m&aacute;y chiếu c&aacute;c điểm tr&ecirc;n khu&ocirc;n mặt) v&agrave; flood illuminator (tạo kh&ocirc;ng gian 3D). V&igrave; vậy, chưa r&otilde; ch&uacute;ng sẽ hoạt động như thế n&agrave;o khi được đặt dưới m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/14/1343370/ios-13-face-id_800x480.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">Apple cũng được cho l&agrave; đang l&agrave;m việc tr&ecirc;n hệ thống Touch ID dưới m&agrave;n h&igrave;nh. Kh&ocirc;ng r&otilde; liệu c&ocirc;ng ty sẽ chuyển sang Touch ID hoặc Face ID dưới m&agrave;n h&igrave;nh hay liệu họ c&oacute; &yacute; định trang bị cả hai tr&ecirc;n c&aacute;c mẫu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"iPhone\">iPhone</a>&nbsp;trong tương lai hay kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn c&oacute; đang mong chờ một mẫu iPhone với hệ thống Face ID ẩn dưới m&agrave;n h&igrave;nh kh&ocirc;ng?</span></p>\r\n', 'uploadFiles/iphone-1_800x450-300x200.png', '2'),
(37, 'Thêm thông tin xác nhận OPPO Reno6 sẽ được trang bị chip xử lý Dimensity 1200, camera 64MP và màn hình tần số quét 90Hz', 2, 'Các báo cáo gần đây đã tiết lộ rằng OPPO đang phát triển chiếc dòng Reno6 cho thị trường Trung Quốc. Dòng sản phẩm này bao gồm: Reno6, Reno6 Pro và Reno6 Pro+. ', '3', 1618397470, '<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/14/1343356/opporeno55g_800x450.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:22px;\">C&aacute;c b&aacute;o c&aacute;o gần đ&acirc;y đ&atilde; tiết lộ rằng&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"OPPO\" type=\"OPPO\">OPPO</a>&nbsp;đang ph&aacute;t triển chiếc d&ograve;ng Reno6 cho thị trường Trung Quốc.&nbsp;D&ograve;ng sản phẩm n&agrave;y bao gồm:&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno6\" target=\"_blank\" title=\"Reno6\" type=\"Reno6\">Reno6</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno6-pro\" target=\"_blank\" title=\"Reno6 Pro\" type=\"Reno6 Pro\">Reno6 Pro</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno6-pro-plus\" target=\"_blank\" title=\"Reno6 Pro+\" type=\"Reno6 Pro+\">Reno6 Pro+</a>. Mới đ&acirc;y, leaker Digital Chat Station đ&atilde; chia sẻ c&aacute;c th&ocirc;ng số cấu h&igrave;nh về phi&ecirc;n bản Reno6.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Theo giới thạo tin, Reno6 sở hữu m&agrave;n h&igrave;nh AMOLED 6.55 inch, hỗ trợ độ ph&acirc;n giải Full HD+ v&agrave; tốc độ l&agrave;m tươi m&agrave;n h&igrave;nh 90Hz.&nbsp;M&agrave;n h&igrave;nh c&oacute; c&aacute;c cạnh cong v&agrave; c&oacute; một lỗ kho&eacute;t ở tr&ecirc;n g&oacute;c tr&aacute;i m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mặt trước của m&aacute;y l&agrave; camera selfie độ ph&acirc;n giải 32 MP.&nbsp;Mặt sau của&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;c&oacute; cụm 4 camera, nổi bật l&agrave; camera ch&iacute;nh 64 MP.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Reno6 được cho l&agrave; trang bị bộ vi xử l&yacute; Dimensity 1200 v&agrave; pin 4.500mAh, hỗ trợ sạc nhanh 65W. Dường như m&aacute;y được trang bị cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Kh&ocirc;ng biết bạn c&oacute; mong đợi g&igrave; ở d&ograve;ng Reno6?</span></p>\r\n', 'uploadFiles/opporeno55g_800x450-300x200.png', '2'),
(38, 'Đón chờ sự kiện Mi 11 Lite series ra mắt tại Việt Nam, mời các bạn đoán xem mức giá của sản phẩm bao nhiêu là hợp lý?', 2, 'Vào ngày mai (15/4), tín đồ công nghệ tại Việt Nam sẽ chứng kiến sự hiện diện của dòng sản phẩm mới nhà Xiaomi, đó chính là Mi 11 Lite series.', '3', 1618397437, '<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/14/1343348/mi-11-lite_800x450.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Xiaomi Mi 11 Lite (nguồn: Gsmarena)</span></em></p>\r\n\r\n<p><span style=\"font-size:22px;\">V&agrave;o ng&agrave;y mai (15/4), t&iacute;n đồ c&ocirc;ng nghệ tại Việt Nam sẽ chứng kiến sự hiện diện của d&ograve;ng sản phẩm mới nh&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" title=\"Xiaomi\" type=\"Xiaomi\">Xiaomi</a>, đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-11-lite-4g\" target=\"_blank\" title=\"Mi 11 Lite\" type=\"Mi 11 Lite\">Mi 11 Lite</a>&nbsp;series. Trước thời điểm ra mắt, ch&uacute;ng ta c&ugrave;ng điểm qua một số th&ocirc;ng tin quan trọng về&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;n&agrave;y nh&eacute;!</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Dự kiến, sự kiện ra mắt Mi 11 Lite series sẽ được tổ chức v&agrave;o l&uacute;c&nbsp;14:15 - 16:00 giờ, ng&agrave;y 15/04 v&agrave; diễn ra tại&nbsp;Pavillon Ho&agrave;ng Văn Thụ, Ph&uacute; Nhuận, TP.HCM.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mi 11 Lite series l&agrave; d&ograve;ng sản phẩm hướng tới người d&ugrave;ng trẻ năng động, với nhiều điểm nhấn ấn tượng. Vẫn chưa r&otilde; Xiaomi sẽ ph&aacute;t h&agrave;nh cả hai phi&ecirc;n bản 4G v&agrave; 5G tại thị trường nước ta hay chỉ mỗi phi&ecirc;n bản 4G.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cụ thể, Mi 11 Lite 4G sở hữu ngoại h&igrave;nh b&oacute;ng bẩy, trọng lượng 157g v&agrave; độ mỏng 6.81mm ấn tượng cho cảm gi&aacute;c thoải m&aacute;i trong l&ograve;ng b&agrave;n tay. M&aacute;y trang bị m&agrave;n h&igrave;nh&nbsp;AMOLED k&iacute;ch thước 6.55 inch Full HD+, hỗ trợ c&ocirc;ng nghệ HDR10+, tốc độ l&agrave;m tươi 90Hz v&agrave; chứng nhận bảo vệ mắt T&Uuml;V Rheinland.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"https://cdn.tgdd.vn/Files/2021/04/14/1343348/xiaomi-mi-11-lite_800x534.jpg\" style=\"width: 100%; height: auto;\" /><em><span style=\"font-size:16px;\">Mi 11 Lite 4G (nguồn: xda-developers)</span></em></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cung cấp sức mạnh cho Mi 11 Lite 4G l&agrave; bộ vi xử l&yacute; Snapdragon 732G, kết hợp với bộ nhớ RAM 8 GB v&agrave; bộ nhớ trong 128 GB.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mặt sau của m&aacute;y l&agrave; hệ thống 3 camera với camera ch&iacute;nh 64 MP, camera g&oacute;c si&ecirc;u rộng 8 MP v&agrave; một camera macro 5MP. Mặt trước của m&aacute;y l&agrave; camera selfie độ ph&acirc;n giải 16 MP.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cuối c&ugrave;ng, Mi 11 Lite 4G được cung cấp năng lượng bởi vi&ecirc;n pin 4.250 mAh, hỗ trợ sạc nhanh 33W. Kh&ocirc;ng biết bạn đo&aacute;n gi&aacute; của Mi 11 Lite series l&agrave; bao nhi&ecirc;u tại thị trường nước ta?</span></p>\r\n', 'uploadFiles/mi-11-lite_800x450-300x200.png', '2');

