--
-- Cấu trúc bảng cho bảng `tbl_products`
--

CREATE TABLE `tbl_products` (
  `code` varchar(20) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `thumb_url` varchar(100) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `price` int(10) NOT NULL,
  `old_price` int(10) DEFAULT NULL,
  `product_name` varchar(1000) COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `author` enum('1','2','3') COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `product_desc` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `product_detail` mediumtext COLLATE utf8mb4_vietnamese_ci NOT NULL,
  `created_date` int(11) NOT NULL,
  `cat_id` int(11) NOT NULL,
  `brand_id` int(11) NOT NULL,
  `status` enum('1','2','3') COLLATE utf8mb4_vietnamese_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_vietnamese_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_products`
--

INSERT INTO `tbl_products` (`code`, `thumb_url`, `price`, `old_price`, `product_name`, `author`, `product_desc`, `product_detail`, `created_date`, `cat_id`, `brand_id`, `status`) VALUES
('ISMART00001', 'uploadFiles/img-pro-19-Copy.png', 13590000, 15000000, 'Acer Aspire 5 A514 54 33WY i3 1115G4 (NX.A23SV.00J)', '3', '<p>*CPU Intel Core i3 Tiger Lake</p>\r\n\r\n<p>*RAM 4 GB</p>\r\n\r\n<p>*SSD 256GB NVMe PCIe</p>\r\n\r\n<p>*Card m&agrave;n h&igrave;nh&nbsp;Intel UHD Graphics</p>\r\n', '<p><span style=\"font-size:22px;\"><a href=\"https://www.thegioididong.com/laptop/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j\" target=\"_blank\" title=\"Laptop Acer Aspire 5 A514 54 33WY i3 (NX.A23SV.00J) \">Laptop Acer Aspire 5 A514 54 33WY i3 (NX.A23SV.00J)</a>&nbsp;l&agrave; chiếc laptop mỏng nhẹ đ&aacute;p ứng ho&agrave;n hảo nhu cầu học tập v&agrave; c&ocirc;ng việc văn ph&ograve;ng của bạn, sở hữu vi xử l&iacute; Intel thế hệ 11, ổ cứng SSD 256 GB cực nhanh v&agrave; thiết kế sang trọng, di động tối ưu.</span></p>\r\n\r\n<p><strong><span style=\"font-size:22px;\">Thiết kế si&ecirc;u mỏng nhẹ cao cấp</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thi&ecirc;́t k&ecirc;́ t&ocirc;́i giản của&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer-aspire\" target=\"_blank\" title=\"Laptop Acer Aspire đang kinh doanh tại Thế Giới Di Động\">laptop Acer Aspire 5</a>&nbsp;với độ mỏng chỉ&nbsp;<strong>17.95 mm</strong>, trọng lượng&nbsp;<strong>1.46 kg</strong>&nbsp;được ho&agrave;n thiện tỉ mỉ với vỏ nhựa v&agrave; nắp lưng kim loại đem đến vẻ đẳng cấp, sang trọng cho Acer Aspire 5. Đ&acirc;y l&agrave; một người bạn đồng h&agrave;nh kh&aacute; l&yacute; tưởng cho c&aacute;c bạn học sinh, sinh vi&ecirc;n năng động khi c&oacute; thể cất gọn chiếc laptop v&agrave;o balo v&agrave; đi khắp nơi</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-272820-092804.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">Chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop-acer\" target=\"_blank\" title=\"Laptop Acer đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh x&aacute;ch tay Acer</a>&nbsp;mỏng nhẹ n&agrave;y vẫn đảm bảo cho người d&ugrave;ng những cổng kết nối th&ocirc;ng dụng nhất, c&oacute; đến 3 cổng USB 3.2, 1 cổng HDMI v&agrave; 1 cổng USB Type-C tiện lợi trong việc trao đổi dữ liệu với c&aacute;c thiết bị kh&aacute;c</span></p>\r\n\r\n<p><br />\r\n<img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-041220-121236.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">Kết nối kh&ocirc;ng d&acirc;y chuẩn Wi-Fi 6 mang đến tốc độ mạng si&ecirc;u nhanh, xem phim, l&agrave;m việc hay học tập trực tuyến thoải m&aacute;i m&agrave; kh&ocirc;ng lo giật lắc, rớt mạng.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-272820-092809.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><strong><span style=\"font-size:22px;\">Vi xử l&iacute; thế hệ mới, sử dụng tốt cho văn ph&ograve;ng</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px;\">Acer sử dụng con chip Intel&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=core-i3\" target=\"_blank\" title=\"Xem thêm các laptop Core i3 đang bán tại Thegioididong.com\">Core i3</a>&nbsp;</strong>Tiger Lake&nbsp;<a href=\"https://www.thegioididong.com/laptop-cpu-intel-gen-11\" target=\"_blank\" title=\"Xem thêm một số laptop có CPU gen 11\">thế hệ thứ 11</a>&nbsp;mới nhất với xung nhịp trung b&igrave;nh 3.00 GHz, tốc độ tối đa 4.1 GHz kết hợp với bộ nhớ&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop?g=4-gb\" target=\"_blank\" title=\"Laptop được trang bị RAM 4 GB đang kinh doanh tại Thegioididong.com\">RAM 4 GB</a></strong>&nbsp;cho hiệu năng ổn định tr&ecirc;n c&aacute;c ứng dụng học tập, văn ph&ograve;ng như Word, Excel,... c&ugrave;ng với Photoshop, cắt gh&eacute;p video nhẹ cũng kh&aacute; ổn định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Laptop c&oacute; hỗ trợ n&acirc;ng cấp RAM tối đa 20 GB để sử dụng đa t&aacute;c vụ tốt hơn</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-010920-050913.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><a href=\"https://www.thegioididong.com/laptop-o-cung-ssd\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop có SSD tại Thegioididong.com\">Ổ cứng SSD</a>&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" title=\"Xem thêm các laptop có SSD 256 GB\">256 GB</a></strong><a href=\"https://www.thegioididong.com/laptop?g=ssd-256-gb\" target=\"_blank\" title=\"Xem thêm các laptop có SSD 256 GB\">&nbsp;</a>mang đến tốc độ xử l&yacute; nhanh ch&oacute;ng, mượt m&agrave; ở mọi t&aacute;c vụ như khởi động m&aacute;y, mở ứng dụng, tr&igrave;nh duyệt đều chỉ t&iacute;nh bằng gi&acirc;y.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-041220-121240.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><strong><span style=\"font-size:22px;\">M&agrave;n h&igrave;nh viền mỏng, hiển thị sống động</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px;\"><a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Xem thêm các mẫu laptop đang kinh doanh tại thegioididong.com\">Laptop</a>&nbsp;sử dụng m&agrave;n h&igrave;nh&nbsp;<strong><a href=\"https://www.thegioididong.com/laptop-14-inch\" target=\"_blank\" title=\"Xem thêm một số laptop màn hình 14 inch đang được kinh doanh tại Thegioididong.com\">14 inch</a></strong>&nbsp;viền mỏng chỉ gi&uacute;p k&iacute;ch thước tổng thể của Acer Aspire 5 gọn g&agrave;ng hơn rất nhiều. Độ ph&acirc;n giải&nbsp;<strong>Full HD</strong>&nbsp;cung cấp h&igrave;nh ảnh c&oacute; độ n&eacute;t cao, c&ugrave;ng với c&ocirc;ng nghệ chống ch&oacute;i, giảm t&igrave;nh trạng l&oacute;a m&agrave;n h&igrave;nh khi sử dụng ngo&agrave;i trời nắng, chiếc m&aacute;y sẽ đem đến cho bạn những khung h&igrave;nh đẹp để giải tr&iacute; v&agrave; l&agrave;m việc một c&aacute;ch tốt nhất.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&ocirc;ng nghệ độc quyền Acer ComfyView giảm &aacute;nh s&aacute;ng xanh hiệu quả, gi&uacute;p bạn giảm t&igrave;nh trạng đau, mỏi mắt, kh&oacute; chịu khi nh&igrave;n l&acirc;u. B&ecirc;n cạnh đ&oacute;, tấm nền IPS sẽ gi&uacute;p bạn c&oacute; được trải nghiệm quan s&aacute;t với g&oacute;c nh&igrave;n rộng, h&igrave;nh ảnh kh&ocirc;ng biến đổi ở mọi g&oacute;c nh&igrave;n</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-272720-092755.jpg\" style=\"width: 100%; height: auto;\" /></p>\r\n\r\n<p><span style=\"font-size:16px;\">C&ocirc;ng nghệ &acirc;m thanh Acer TrueHarmony đem đến chất &acirc;m tuyệt hảo, &acirc;m bass d&agrave;y v&agrave; chắc, hiệu ứng &acirc;m thanh v&ograve;m cực đ&atilde; tai cho người d&ugrave;ng trải nghiệm nghe nhạc, xem phim cực đ&atilde; tai.</span></p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/44/231082/acer-aspire-5-a514-54-33wy-i3-nxa23sv00j-041220-121244.jpg\" style=\"width: 100%; height: auto;\" /><span style=\"font-size:16px;\">Nh&igrave;n chung, Acer&nbsp;Aspire 5 A514 54 33WY i3 (NX.A23SV.00J) với ngoại h&igrave;nh&nbsp;thanh lịch, cấu h&igrave;nh ổn định, chiếc laptop n&agrave;y ph&ugrave; hợp với c&aacute;c bạn học sinh sinh vi&ecirc;n cần một chiếc&nbsp;<a href=\"https://www.thegioididong.com/laptop?g=mong-nhe-thoi-trang\" target=\"_blank\" title=\"Xem thêm các sản phẩm laptop mỏng nhẹ đang bán tại Thegioididong.com\">laptop mỏng nhẹ</a>&nbsp;dễ di chuyển c&ugrave;ng hiệu năng đủ mạnh để đ&aacute;p ứng nhu cầu học tập, l&agrave;m việc hằng ng&agrave;y</span></p>\r\n', 1618399043, 1, 1, '2'),
